#!/bin/bash\n\nfor REPOSITORY in "$@"\ndo\n    cd "$REPOSITORY" || exit\n\n    # Commit the changes\n    hg commit -A -m "Commit changes $(date)"\n\n    # Push the changes to the remote repository\n    if hg push\n    then\n        logger "hg push success"\n    else\n        logger "hg push failure"\n    fi\ndone