server {\n        listen 80;\n        server_name myDomain.com;\n        \n        location / {\n            proxy_pass http://localhost:8080; # Proxy to your web server\n        }\n    }\n\n    server {\n        listen 80;\n        server_name myApp.myDomain.com;\n\n        location / {\n            proxy_pass http://localhost:9090; # Proxy to your non-web server application\n        }\n    }