GLIBC_VERSION := $(shell getconf GNU_LIBC_VERSION | awk '{print $$2}')\nPTHREAD_VERSION := $(shell getconf GNU_LIBPTHREAD_VERSION | awk '{print $$2}')\nLIB_DIR := /lib/tls\nLIBC := $(LIB_DIR)/libc-$(GLIBC_VERSION).so\nLIBPTHREAD := $(LIB_DIR)/libpthread-$(PTHREAD_VERSION).so\n\n# Check if libraries exist\nifeq ("$(wildcard $(LIBC))", "")\n$(error "libc version $(GLIBC_VERSION) not found in $(LIB_DIR)")\nendif\n\nifeq ("$(wildcard $(LIBPTHREAD))", "")\n$(error "libpthread version $(PTHREAD_VERSION) not found in $(LIB_DIR)")\nendif\n\n# Example rule to link against the libraries\nmy_program: my_program.o\n    $(CC) -o $@ $^ $(LIBC) $(LIBPTHREAD)