ARCH := $(shell uname -m)\n     KERNEL := $(shell uname -r)\n     LIB_DIR := $(HOME)/libs/library-name/$(ARCH)-$(KERNEL)\n     \n     CXXFLAGS := -I$(LIB_DIR)/include\n     LDFLAGS := -L$(LIB_DIR)/lib -lmylibrary\n     \n     all: myprogram\n\n     myprogram: myprogram.o\n         $(CXX) $(LDFLAGS) -o myprogram myprogram.o\n\n     myprogram.o: myprogram.cpp\n         $(CXX) $(CXXFLAGS) -c myprogram.cpp\n\n     clean:\n         rm -f myprogram myprogram.o