# Directories\nCLEAN_DIR = src/clean\nDIRTY_DIR = src/dirty\nBUILD_DIR = build\n\n# Compiler and flags\nCC = gcc\nCLEAN_FLAGS = -ansi -pedantic-errors -Wextra -Wall\nDIRTY_FLAGS = -Wall\n\n# Source files\nCLEAN_SRCS = $(wildcard $(CLEAN_DIR)/*.c)\nDIRTY_SRCS = $(wildcard $(DIRTY_DIR)/*.c)\n\n# Object files\nCLEAN_OBJS = $(patsubst $(CLEAN_DIR)/%.c, $(BUILD_DIR)/%.o, $(CLEAN_SRCS))\nDIRTY_OBJS = $(patsubst $(DIRTY_DIR)/%.c, $(BUILD_DIR)/%.o, $(DIRTY_SRCS))\n\n# Target executable\nTARGET = my_program\n\n# Build rules\nall: $(TARGET)\n\n$(TARGET): $(CLEAN_OBJS) $(DIRTY_OBJS)\n	$(CC) -o $@ $^\n\n$(BUILD_DIR)/%.o: $(CLEAN_DIR)/%.c\n	@mkdir -p $(BUILD_DIR)\n	$(CC) $(CLEAN_FLAGS) -c $< -o $@\n\n$(BUILD_DIR)/%.o: $(DIRTY_DIR)/%.c\n	@mkdir -p $(BUILD_DIR)\n	$(CC) $(DIRTY_FLAGS) -c $< -o $@\n\nclean:\n	rm -rf $(BUILD_DIR) $(TARGET)\n\n.PHONY: all clea