# Define variables\n$contactName = "John Doe"\n$ou = "OU=Contacts,DC=example,DC=com"\n$externalEmailAddress = "johndoe@example.com"\n$alias = "johndoe"\n\n# Create the contact in Active Directory\nNew-MailContact -Name $contactName -ExternalEmailAddress $externalEmailAddress -OrganizationalUnit $ou -Alias $alias\n\n# Ensure the contact is mail-enabled and has the necessary attributes set\nSet-Contact -Identity $contactName -DisplayName $contactName -PrimarySmtpAddress $externalEmailAddress