# Define the source UNC path and the destination local path\n$sourceUNCPath = "\\server\share\file.txt"\n$destinationPath = "C:\path\to\download\file.txt"\n\n# Create a new BITS transfer job\n$job = Start-BitsTransfer -Source $sourceUNCPath -Destination $destinationPath\n\n# Optionally, you can monitor the status of the job\n$job | Get-BitsTransfer\n\n# Complete the transfer job\nComplete-BitsTransfer -BitsJob $job