# Define the application pool name or the virtual directory you are looking for\n$appPoolName = "DefaultAppPool"\n$virtualDirName = "MyVirtualDirectory"\n\n# Query the virtual directories\n$virtualDirs = Get-WmiObject -Namespace "root\MicrosoftIISv2" -Class "IIsWebVirtualDirSetting" | Where-Object { $_.AppPoolId -eq $appPoolName -and $_.Name -like "*$virtualDirName" }\n\n# Output the physical path of the virtual directory\nforeach ($virtualDir in $virtualDirs) {\n    Write-Output "Virtual Directory: $($virtualDir.Name)"\n    Write-Output "Physical Path: $($virtualDir.Path)"\n}