# Delete the existing SSP (if any references are blocking, you might need to force remove)\n   Remove-SPServiceApplication -Identity "<OldSSPName>" -RemoveData -Confirm:$false\n\n   # Create a new SSP\n   $newSSP = New-SPServiceApplication -Name "<NewSSPName>" -ApplicationPool "<AppPoolName>" -DatabaseName "<NewDatabaseName>"\n   \n   # Associate services to the new SSP\n   $proxyGroup = Get-SPServiceApplicationProxyGroup -Default\n   Add-SPServiceApplicationProxyGroupMember -Identity $proxyGroup -Member $newSSP