$path = "C:\Inetpub\wwwroot\wss\VirtualDirectories\8443"\n$account = "DOMAIN\mosssvc"\n\n# Check permissions\n$acl = Get-Acl $path\n$acl.Access | Where-Object { $_.IdentityReference -eq $account }\n\n# Set permissions if necessary\n$permission = "FullControl"\n$inheritance = "ContainerInherit,ObjectInherit"\n$propagation = "None"\n\n$rule = New-Object System.Security.AccessControl.FileSystemAccessRule($account, $permission, $inheritance, $propagation, "Allow")\n$acl.SetAccessRule($rule)\nSet-Acl -Path $path -AclObject $acl