# PowerShell script to bind an SSL certificate to the SSRS web service\n$CertThumbprint = "YOUR_CERTIFICATE_THUMBPRINT"\n$SSRSUrl = "https://yourreportserver:443"\n\n# Get the certificate from the store\n$Cert = Get-ChildItem -Path cert:\LocalMachine\My | Where-Object { $_.Thumbprint -eq $CertThumbprint }\n\n# Bind the certificate to the SSRS URL\nNew-WebBinding -Name "ReportServer" -IPAddress "*" -Port 443 -Protocol https\nSet-WebConfiguration -Filter "/system.applicationHost/sites/site[@name='ReportServer']/bindings/binding[@protocol='https']" `\n    -Value @{bindingInformation="*:443:";certificateHash=$Cert.Thumbprint;certificateStoreName="My"}\n\n# Restart the SSRS service\nRestart-Service -Name "SQLServerReportingServices"