# Import the Exchange module\nImport-Module ExchangeOnlineManagement\n\n# Connect to Exchange Online\nConnect-ExchangeOnline -UserPrincipalName <your-admin-account> -ShowProgress $true\n\n# Get the SMTP address for a user\n$userSMTP = Get-Mailbox -Identity <username> | Select-Object -ExpandProperty PrimarySmtpAddress\n\n# Display the SMTP address\n$userSMTP\n\n# Disconnect from Exchange Online\nDisconnect-ExchangeOnline -Confirm:$false