# Path to the folder\n$folderPath = "C:\path\to\your\folder"\n\n# Path to temporarily move the remaining file\n$tempFilePath = "C:\path\to\temp\file"\n\n# Move the remaining file to a temporary location\nMove-Item "$folderPath\yourfile.ext" $tempFilePath\n\n# Remove the folder\nRemove-Item $folderPath -Recurse\n\n# Recreate the folder\nNew-Item -ItemType Directory -Path $folderPath\n\n# Move the file back to the newly created folder\nMove-Item $tempFilePath "$folderPath\yourfile.ext"