# Add references to the SharePoint Client assemblies\nAdd-Type -Path "C:\Path\To\Microsoft.SharePoint.Client.dll"\nAdd-Type -Path "C:\Path\To\Microsoft.SharePoint.Client.Runtime.dll"\n\n# SharePoint Online site URL\n$siteUrl = "https://yoursharepointsiteurl"\n\n# List name\n$listName = "Your List Name"\n\n# Credentials\n$username = "yourusername@yourdomain.com"\n$password = "yourpassword"\n$securePassword = ConvertTo-SecureString $password -AsPlainText -Force\n$credentials = New-Object Microsoft.SharePoint.Client.SharePointOnlineCredentials($username, $securePassword)\n\n# Connect to the SharePoint site\n$context = New-Object Microsoft.SharePoint.Client.ClientContext($siteUrl)\n$context.Credentials = $credentials\n\n# Get the list\n$list = $context.Web.Lists.GetByTitle($listName)\n$context.Load($list)\n$context.ExecuteQuery()\n\n# Get the items from the list\n$query = [Microsoft.SharePoint.Client.CamlQuery]::CreateAllItemsQuery()\n$items = $list.GetItems($query)\n$context.Load($items)\n$context.ExecuteQuery()\n\n# Create an XML document\n[xml]$xmlDoc = New-Object System.Xml.XmlDocument\n$xmlRoot = $xmlDoc.CreateElement("ListItems")\n$xmlDoc.AppendChild($xmlRoot)\n\nforeach ($item in $items) {\n    $xmlItem = $xmlDoc.CreateElement("Item")\n    foreach ($field in $item.FieldValues.Keys) {\n        $xmlField = $xmlDoc.CreateElement($field)\n        $xmlField.InnerText = $item[$field].ToString()\n        $xmlItem.AppendChild($xmlField)\n    }\n    $xmlRoot.AppendChild($xmlItem)\n}\n\n# Save the XML document to a file\n$xmlDoc.Save("C:\Path\To\OutputFile.xml")\n\nWrite-Host "Export completed successfully!"