# Define paths and credentials\n$svnRepoUrl = "https://svn.example.com/repo"\n$svnCheckoutPath = "C:\path\to\svn\checkout"\n$tfsRepoPath = "C:\path\to\tfs\workspace"\n$tfsCollectionUrl = "http://tfs.example.com:8080/tfs/DefaultCollection"\n$tfsProject = "YourProject"\n$tfsUsername = "tfs-username"\n$tfsPassword = "tfs-password"\n\n# SVN checkout\nsvn checkout $svnRepoUrl $svnCheckoutPath --username your-svn-username --password your-svn-password\n\n# Copy SVN contents to TFS workspace\nCopy-Item -Path "$svnCheckoutPath\*" -Destination $tfsRepoPath -Recurse -Force\n\n# Navigate to TFS workspace\nSet-Location $tfsRepoPath\n\n# TFS check-in\ntf add * --recursive\ntf checkin -comment:"Automated check-in from SVN" -login:$tfsUsername,$tfsPassword\n\n# Optional: Clean up SVN checkout directory\nRemove-Item -Path $svnCheckoutPath -Recurse -Force