# Get all .aspx files in the project directory and subdirectories\n$files = Get-ChildItem -Path . -Recurse -Include *.aspx, *.aspx.cs, *.aspx.vb\n\nforeach ($file in $files) {\n    # Read the content of the file as Windows-1251\n    $content = Get-Content -Path $file.FullName -Encoding Windows-1251\n\n    # Write the content back to the file as UTF-8\n    Set-Content -Path $file.FullName -Value $content -Encoding utf8\n\n    Write-Host "Converted $($file.FullName) to UTF-8"\n}\n\nWrite-Host "Conversion complete."