# Load SharePoint Online Client SDK\nAdd-Type -Path "C:\Program Files\Common Files\microsoft shared\Web Server Extensions\16\ISAPI\Microsoft.SharePoint.Client.dll"\nAdd-Type -Path "C:\Program Files\Common Files\microsoft shared\Web Server Extensions\16\ISAPI\Microsoft.SharePoint.Client.Runtime.dll"\n\n# Set parameters\n$siteUrl = "https://yourTenant.sharepoint.com/sites/yourSite"\n$listName = "Your List Name"\n$username = "yourUser@yourTenant.onmicrosoft.com"\n$password = "yourPassword"\n\n# Create credentials\n$securePassword = ConvertTo-SecureString $password -AsPlainText -Force\n$cred = New-Object Microsoft.SharePoint.Client.SharePointOnlineCredentials($username, $securePassword)\n\n# Connect to SharePoint Online site\n$context = New-Object Microsoft.SharePoint.Client.ClientContext($siteUrl)\n$context.Credentials = $cred\n\n# Get the list\n$list = $context.Web.Lists.GetByTitle($listName)\n$context.Load($list)\n$context.ExecuteQuery()\n\n# Get alerts for the list\n$alerts = $context.Web.Alerts\n$context.Load($alerts)\n$context.ExecuteQuery()\n\n# Display alerts\nforeach ($alert in $alerts)\n{\n    if ($alert.ListID -eq $list.Id)\n    {\n        Write-Host "User: $($alert.User.LoginName), Alert Title: $($alert.Title)"\n    }\n}