# Open IIS Manager and navigate to your FTP site\n# In the Actions pane, click on "FTP Firewall Support"\n\n# Specify the Data Channel Port Range\nSet-WebConfigurationProperty -filter "system.ftpServer/firewallSupport" -name "lowDataChannelPort" -value 5000\nSet-WebConfigurationProperty -filter "system.ftpServer/firewallSupport" -name "highDataChannelPort" -value 5100\n\n# Specify the External IP Address\nSet-WebConfigurationProperty -filter "system.ftpServer/firewallSupport" -name "externalIp4Address" -value "203.0.113.5"\n\n# Restart the FTP service to apply changes\nRestart-WebItem 'IIS:\sites\YourFtpSiteName'