# PowerShell script to delete and regenerate the .designer.cs file\n$projectPath = "C:\Path\To\Your\Project"\n$dbmlFile = "$projectPath\YourDatabaseFile.dbml"\n$designerFile = "$dbmlFile.designer.cs"\n\nif (Test-Path $designerFile) {\n    Remove-Item $designerFile\n    Write-Host "Deleted $designerFile"\n} else {\n    Write-Host "$designerFile does not exist"\n}\n\n# This command assumes you have the correct tools installed and configured\n& "C:\Path\To\VisualStudio\devenv.exe" /Command "Project.RunCustomTool $dbmlFile"