# Define the URL of your MOSS 2007 site\n    $siteUrl = "http://your-intranet-site-url"\n\n    # Define the email parameters\n    $emailFrom = "monitoring@yourdomain.com"\n    $emailTo = "admin@yourdomain.com"\n    $smtpServer = "smtp.yourdomain.com"\n    $subject = "MOSS 2007 Site Down Notification"\n    $body = "The MOSS 2007 site is currently down: $siteUrl"\n\n    # Try to access the site\n    try {\n        $request = [System.Net.WebRequest]::Create($siteUrl)\n        $response = $request.GetResponse()\n        $statusCode = $response.StatusCode\n        $response.Close()\n    } catch {\n        # If an exception occurs, the site is down\n        $statusCode = "Site Down"\n    }\n\n    # If the site is down, send an email notification\n    if ($statusCode -ne 200) {\n        $smtp = New-Object Net.Mail.SmtpClient($smtpServer)\n        $message = New-Object Net.Mail.MailMessage($emailFrom, $emailTo, $subject, $body)\n        $smtp.Send($message)\n    }