# Define the path to your project directory and project file\n$projectDir = "C:\Path\To\Your\Project"\n$projectFile = "C:\Path\To\Your\Project\YourProject.csproj"\n\n# Load the project file and extract the list of included files\n[xml]$proj = Get-Content $projectFile\n$includedFiles = $proj.Project.ItemGroup | ForEach-Object {\n    $_.Compile, $_.Content, $_.None, $_.EmbeddedResource\n} | Where-Object { $_ -ne $null } | ForEach-Object { $_.Include }\n\n# Convert to a set for faster lookup\n$includedFilesSet = @{}\n$includedFiles | ForEach-Object { $includedFilesSet[$_] = $true }\n\n# Get all files in the project directory\n$allFiles = Get-ChildItem -Path $projectDir -Recurse -File\n\n# Find and delete excluded files\n$allFiles | ForEach-Object {\n    $relativePath = $_.FullName.Substring($projectDir.Length + 1).Replace("\", "/")\n    if (-not $includedFilesSet.ContainsKey($relativePath)) {\n        Write-Host "Deleting excluded file: $($_.FullName)"\n        Remove-Item -Path $_.FullName -Force\n    }\n}