# Install SMTP Server feature\nInstall-WindowsFeature -Name SMTP-Server -IncludeAllSubFeature -IncludeManagementTools\n\n# Import the required module\nImport-Module ServerManager\n\n# Add relay restrictions\n$relayIpList = @("192.168.1.10", "192.168.1.20") # Replace with your server IPs\n$smtpServer = Get-WmiObject -Namespace "root\MicrosoftIISv2" -Class "IISSMTPServerSetting"\n$smtpServer.RelayIpList = $relayIpList\n$smtpServer.Put()\n\n# Configure Smart Host\n$smtpServer.SmartHost = "corporate.smtp.server"  # Replace with your corporate SMTP relay server\n$smtpServer.Put()\n\n# Ensure port 25 is open on the firewall\nNew-NetFirewallRule -DisplayName "Allow SMTP" -Direction Inbound -Protocol TCP -LocalPort 25 -Action Allow