# Increase the maximum number of concurrent connections\nSet-WebConfigurationProperty -pspath 'MACHINE/WEBROOT/APPHOST' -filter "system.applicationHost/sites/siteDefaults/limits" -name "connectionTimeout" -value "00:01:00"\n\n# Increase the maximum number of worker processes\nSet-WebConfigurationProperty -pspath 'MACHINE/WEBROOT/APPHOST' -filter "system.applicationHost/applicationPools/applicationPoolDefaults" -name "processModel.maxProcesses" -value "4"\n\n# Adjust ASP.NET request queue limit (if using ASP.NET)\nSet-WebConfigurationProperty -pspath 'MACHINE/WEBROOT/APPHOST' -filter "system.webServer/aspNetCore" -name "requestQueueLimit" -value "5000"