$ReportServerUri = "http://YourProductionServer/ReportServer/ReportService2010.asmx"\n   $Folder = "/YourTargetFolder"\n   $ReportsPath = "C:\Path\To\Your\Reports"\n\n   $reportingService = New-WebServiceProxy -Uri $ReportServerUri -UseDefaultCredential\n\n   foreach ($report in Get-ChildItem -Path $ReportsPath -Filter *.rdl) {\n       $reportDefinition = [System.IO.File]::ReadAllBytes($report.FullName)\n       $reportName = [System.IO.Path]::GetFileNameWithoutExtension($report.Name)\n       $reportingService.CreateCatalogItem("Report", $reportName, $Folder, $true, $reportDefinition, $null)\n       Write-Output "Deployed: $reportName"\n   }