# List of database connection strings\n$databases = @(\n    "Server=server1;Database=db1;User Id=your_user;Password=your_password;",\n    "Server=server2;Database=db2;User Id=your_user;Password=your_password;",\n    "Server=server3;Database=db3;User Id=your_user;Password=your_password;"\n    # Add all 18 database connection strings here\n)\n\n# Path to the SQL script\n$sqlScriptPath = "C:\path\to\your\script.sql"\n\n# Read the SQL script\n$sqlScript = Get-Content -Path $sqlScriptPath -Raw\n\nforeach ($db in $databases) {\n    # Execute the script on each database\n    Invoke-Sqlcmd -ConnectionString $db -Query $sqlScript\n}