# Create a new application pool\nNew-WebAppPool -Name "MyNewAppPool"\n\n# Assign the first web service to the new application pool\nSet-ItemProperty -Path "IIS:\Sites\Default Web Site\MyWebService1" -Name "applicationPool" -Value "MyNewAppPool"\n\n# Create another application pool if needed\nNew-WebAppPool -Name "MySecondAppPool"\n\n# Assign the second web service to the second application pool\nSet-ItemProperty -Path "IIS:\Sites\Default Web Site\MyWebService2" -Name "applicationPool" -Value "MySecondAppPool"