# PowerShell script to uncompress .Z file\n$compressedFile = "C:\path\to\file.Z"\n$uncompressedFile = "C:\path\to\file.txt"\n\n# Use gzip to uncompress the file\ngzip -d $compressedFile\n\n# SQL Server BULK INSERT command\nInvoke-Sqlcmd -Query @"\nBULK INSERT YourTable\nFROM '$uncompressedFile'\nWITH (\n    FIELDTERMINATOR = ',',\n    ROWTERMINATOR = '\n',\n    FIRSTROW = 2\n);\n"@ -ServerInstance "YourServerInstance" -Database "YourDatabase"