# Load NAudio library\nAdd-Type -Path "path_to_NAudio.dll"\n\n# Function to set default audio device\nfunction Set-DefaultAudioDevice {\n    param (\n        [Parameter(Mandatory=$true)]\n        [string]$deviceName\n    )\n\n    $enumerator = New-Object NAudio.CoreAudioApi.MMDeviceEnumerator\n    $devices = $enumerator.EnumerateAudioEndPoints([NAudio.CoreAudioApi.DataFlow]::Render, [NAudio.CoreAudioApi.DeviceState]::Active)\n\n    foreach ($device in $devices) {\n        if ($device.FriendlyName -eq $deviceName) {\n            $deviceID = $device.ID\n            break\n        }\n    }\n\n    if ($deviceID) {\n        (New-Object -ComObject WScript.Shell).RegWrite("HKEY_CURRENT_USER\Software\Microsoft\Multimedia\Sound Mapper\Playback", $deviceID, "REG_SZ")\n        Write-Output "Default audio device set to $deviceName"\n    } else {\n        Write-Error "Device not found: $deviceName"\n    }\n}\n\n# Example usage\nSet-DefaultAudioDevice -deviceName "Your Sound Device Name"