# Open SharePoint Management Shell as an Administrator\n# Use PowerShell to update the configuration\n\n$spWebService = [Microsoft.SharePoint.Administration.SPWebService]::ContentService\n$spWebService.WebApplications | ForEach-Object {\n    $_.IisSettings.Values | ForEach-Object {\n        $_.Path = "D:\SharePoint"\n        $_.Update()\n    }\n}\n\n# Restart the SharePoint services