#!/bin/sh\n\n# Command to execute\n# ./mybashcode.sh test.nbq\n\nnbqfile=$1   \nnbqbase=$(basename "$nbqfile" .nbq)\ntaglistfiletemp="${nbqbase}taglist_temp"\ntaglistfile="${nbqbase}taglist"\n\n./myccode "$nbqfile" | \\nsort | \\nuniq -c | \\nawk '{print $2}' > "$taglistfiletemp"\n\nnoftags=$(wc -l < "$taglistfiletemp")\necho "$noftags"\n\n# We want to append output of noftags to the first line of taglistfile\nsed "1i\\\n$noftags" "$taglistfiletemp" > "$taglistfile"