#!/bin/bash\n\n# FTP server details\nFTP_SERVER="ftp.example.com"\nFTP_USER="your_ftp_username"\nFTP_PASS="your_ftp_password"\nFTP_DIR="backup_directory"  # The directory where backups are stored\n\n# Backup file details\nBACKUP_FILE="backup_$(date +%Y%m%d%H%M%S).tar.gz"\n\n# Create the backup (webfiles + database dump)\ntar -czf /path/to/backup/$BACKUP_FILE /path/to/webfiles /path/to/database_dump.sql\n\n# Upload the backup to the FTP server\nftp -inv $FTP_SERVER <<EOF\nuser $FTP_USER $FTP_PASS\ncd $FTP_DIR\nput /path/to/backup/$BACKUP_FILE\nbye\nEOF\n\n# List files on the FTP server and delete the oldest ones if there are more than 10\nftp -inv $FTP_SERVER <<EOF | grep -E '^-|^d' | awk '{print $9}' | sort | head -n -10 | while read file; do\nuser $FTP_USER $FTP_PASS\ncd $FTP_DIR\nmdelete $file\nbye\nEOF