#!/bin/bash\n\n# Set the VSS project path\nVSS_PROJECT_PATH="$1"\n\n# Check if the project path is provided\nif [ -z "$VSS_PROJECT_PATH" ]; then\n  echo "Usage: $0 <VSS_PROJECT_PATH>"\n  exit 1\nfi\n\n# Set the path to the ss.exe utility\nSS_EXE_PATH="/path/to/ss.exe"\n\n# Set the VSS database path\nVSS_DB_PATH="/path/to/vss/db"\n\n# Set the VSS user credentials\nVSS_USER="your_username"\nVSS_PASSWORD="your_password"\n\n# Run the ss.exe command to list checked out files\n$SS_EXE_PATH Status "$VSS_PROJECT_PATH" -R -Y"$VSS_USER","$VSS_PASSWORD" -I-Y | grep "Checked out"\n\n# Example usage\n# ./find_checked_out_files.sh "$/MyProject"