#!/bin/bash\n\n# Define your source and header files\nSOURCE_FILES="main.cpp"\nHEADER_FILES="header.h"\n\n# Preprocess each source file using the Perl script\nfor file in $SOURCE_FILES $HEADER_FILES; do\n    perl filter.pl "$file" > "$file.filtered"\ndone\n\n# Compile the preprocessed files with gcc\ngcc main.cpp.filtered -o output -I. -include header.h.filtered\n\n# Clean up temporary filtered files\nrm *.filtered