#!/bin/bash\n\n# Define the file path\nFILE_PATH="/home/sites/example.com/www/files/backup.csv"\n\n# Remove the existing file if it exists\nif [ -f "$FILE_PATH" ]; then\n    rm "$FILE_PATH"\nfi\n\n# Execute the MySQL command to create the new CSV file\nmysql -u username -p -e "\nSELECT * \nINTO OUTFILE '$FILE_PATH'\nFIELDS TERMINATED BY ',' OPTIONALLY ENCLOSED BY '\"'\nLINES TERMINATED BY '\n'\nFROM your_table_name;\n"