#!/bin/sh\n\nfile=$1\nn=$2\n\n# if number is zero exit\nif [ "$n" -eq "0" ]; then\n    exit 0\nfi\n\necho "Generating list of $n people."\n\n# Use awk to generate the list more efficiently\nawk -v n=$n 'BEGIN { \n    srand(); \n    for (i = 1; i <= n; i++) { \n        num = int(rand() * 75) + 15; \n        printf "name%d %d (###)###-####\n", i, num; \n    } \n}' > $file\n\necho "List generated."