# Create virtual ports\nsocat -d -d pty,raw,echo=0,link=/tmp/virtualport1 pty,raw,echo=0,link=/tmp/virtualport2 &\n\n# Monitor and log data\nsocat -d -d file:/dev/ttyS0,raw,echo=0 SYSTEM:'tee /tmp/logfile > /tmp/virtualport1' &\nsocat -d -d file:/dev/ttyS1,raw,echo=0 SYSTEM:'tee /tmp/logfile > /tmp/virtualport2' &