# Check disk health\nsudo smartctl -a /dev/sdX\n\n# Check PostgreSQL logs\ntail -f /var/log/postgresql/postgresql-13-main.log\n\n# Run VACUUM and REINDEX\npsql -U your_username -d your_database -c "VACUUM FULL your_table_name;"\npsql -U your_username -d your_database -c "REINDEX TABLE your_table_name;"\n\n# Dump and restore the database\npg_dump -Fc your_database_name > your_database_dump.dump\npg_restore -d your_new_database_name your_database_dump.dump