#!/bin/bash\n\n# Simple function to test\nhello() {\n  echo "Hello, $1!"\n}\n\n# TAP header\necho "1..2"\n\n# Test 1: hello function with argument "World"\nresult=$(hello "World")\nif [ "$result" = "Hello, World!" ]; then\n  echo "ok 1 - hello function with 'World'"\nelse\n  echo "not ok 1 - hello function with 'World'"\nfi\n\n# Test 2: hello function with no argument\nresult=$(hello "")\nif [ "$result" = "Hello, !" ]; then\n  echo "ok 2 - hello function with ''"\nelse\n  echo "not ok 2 - hello function with ''"\nfi