#!/bin/bash\n\n   BUG_ID=12345\n   COMMENT="This is a new comment"\n   BUGZILLA_URL="http://your-bugzilla-url/jsonrpc.cgi"\n\n   DATA=$(cat <<EOF\n   {\n       "method": "Bug.add_comment",\n       "params": [{\n           "id": $BUG_ID,\n           "comment": "$COMMENT"\n       }],\n       "id": 1\n   }\n   EOF\n   )\n\n   curl -X POST -H "Content-Type: application/json" -d "$DATA" $BUGZILLA_URL