#!/bin/bash\n\n# Define the source file and the line range\nsource_file="source.txt"\n\n# Extract lines 6-15 from the source file\nextracted_lines=$(awk 'NR>=6 && NR<=15' "$source_file")\n\n# Iterate over the 2000 target files\nfor target_file in target_files/*.txt; do\n  # Create a temporary file\n  tmp_file=$(mktemp)\n\n  # Delete lines 5-15 from the target file and save to temporary file\n  sed '5,15d' "$target_file" > "$tmp_file"\n\n  # Insert the extracted lines into the temporary file after line 4\n  awk -v lines="$extracted_lines" 'NR==4 {print; print lines} NR!=4' "$tmp_file" > "$target_file"\n\n  # Clean up the temporary file\n  rm "$tmp_file"\ndone