# Start MongoDB instances\nmongod --shardsvr --replSet shard1 --dbpath /data/shard1 --port 27017\nmongod --shardsvr --replSet shard2 --dbpath /data/shard2 --port 27018\n\n# Start Config Servers\nmongod --configsvr --replSet csrs --dbpath /data/csrs --port 27019\n\n# Start the mongos instance\nmongos --configdb csrs/hostname:27019\n\n# Connect to mongos and configure sharding\nmongo --port 27017\nsh.addShard("shard1/hostname:27017")\nsh.addShard("shard2/hostname:27018")\nsh.enableSharding("mydatabase")