# GET request\n     curl -X GET http://example.com/resource\n\n     # POST request with JSON data\n     curl -X POST http://example.com/resource -H "Content-Type: application/json" -d '{"key":"value"}'\n\n     # PUT request\n     curl -X PUT http://example.com/resource -H "Content-Type: application/json" -d '{"key":"value"}'\n\n     # DELETE request\n     curl -X DELETE http://example.com/resource