# deploy.sh\n    #!/bin/bash\n\n    # Variables\n    LOCAL_PATH="/path/to/your/local/checkout"\n    REMOTE_PATH="user@yourserver:/path/to/your/remote/application"\n    EXCLUDE_DIRS="uploads"\n\n    # Update local copy from SVN\n    svn update $LOCAL_PATH\n\n    # Rsync the files to the remote server, excluding specified directories\n    rsync -avz --exclude $EXCLUDE_DIRS $LOCAL_PATH/ $REMOTE_PATH/