# deploy-ftp.sh\n    #!/bin/bash\n\n    # Variables\n    LOCAL_PATH="/path/to/your/local/checkout"\n    REMOTE_PATH="/path/to/your/remote/application"\n    FTP_HOST="ftp.yourserver.com"\n    FTP_USER="your-ftp-user"\n    FTP_PASS="your-ftp-password"\n    EXCLUDE_DIRS="uploads"\n\n    # Update local copy from SVN\n    svn update $LOCAL_PATH\n\n    # Use lftp to mirror files, excluding the uploads directory\n    lftp -e "mirror -R --exclude $EXCLUDE_DIRS $LOCAL_PATH $REMOTE_PATH; quit" -u $FTP_USER,$FTP_PASS $FTP_HOST