#!/bin/bash\n\n# MySQL credentials\nMYSQL_USER="your_username"\nMYSQL_PASSWORD="your_password"\nMYSQL_DATABASE="your_database"\n\n# Get the list of tables in the correct order based on foreign key constraints\nTABLES=$(mysql -u $MYSQL_USER -p$MYSQL_PASSWORD $MYSQL_DATABASE -Bse "SELECT table_name FROM information_schema.tables WHERE table_schema = '$MYSQL_DATABASE' ORDER BY table_name;")\n\n# Truncate each table\nfor TABLE in $TABLES; do\n    echo "Truncating table $TABLE"\n    mysql -u $MYSQL_USER -p$MYSQL_PASSWORD $MYSQL_DATABASE -e "SET FOREIGN_KEY_CHECKS = 0; TRUNCATE TABLE $TABLE; SET FOREIGN_KEY_CHECKS = 1;"\ndone\n\necho "All tables truncated successfully."