# Install WireGuard on each peer\nsudo apt-get install wireguard\n\n# Generate keys for each peer\nwg genkey | tee privatekey | wg pubkey > publickey\n\n# Example WireGuard configuration for a peer (wg0.conf)\n[Interface]\nPrivateKey = <peer_private_key>\nAddress = 10.0.0.1/24   # Assign a private IP from the chosen range\nListenPort = 51820\n\n[Peer]\nPublicKey = <other_peer_public_key>\nEndpoint = <other_peer_ip>:51820\nAllowedIPs = 10.0.0.0/24