#!/bin/bash\n\n   LOCAL_DIR="/path/to/local/dir"\n   REMOTE_DIR="user@remote_server:/path/to/remote/dir"\n   RSYNC_OPTIONS="-avz --delete"\n\n   # Function to perform the initial sync\n   sync_dirs() {\n       rsync $RSYNC_OPTIONS "$LOCAL_DIR" "$REMOTE_DIR"\n   }\n\n   # Initial sync\n   sync_dirs\n\n   # Monitor the local directory for changes and sync upon changes\n   inotifywait -m -r -e modify,create,delete,move "$LOCAL_DIR" --format '%w%f' | while read CHANGE; do\n       echo "Detected change: $CHANGE"\n       sync_dirs\n   done