#!/bin/sh\n\n# Create a temporary file\ntemp_file="/var/tmp/toto"\n\n# Get the current time and subtract 58 minutes\ntimestamp=$(date -d '58 minutes ago' +%Y%m%d%H%M.%S)\n\n# Create the reference file with the calculated timestamp\ntouch -t "$timestamp" "$temp_file"\n\n# Check if the target file is older than the reference file\ntarget_file="./logs_dir/your_log_file.log"\n\nif [ -e "$target_file" ]; then\n  if [ "$(find "$target_file" ! -newer "$temp_file" -print)" ]; then\n    echo "The file $target_file is more than 58 minutes old."\n  else\n    echo "The file $target_file is not more than 58 minutes old."\n  fi\nelse\n  echo "The file $target_file does not exist."\nfi\n\n# Clean up\nrm "$temp_file"