#!/bin/bash\n\nDB_NAME="your_database"\nDB_USER="your_username"\nDB_PASS="your_password"\nOUTPUT_DIR="/path/to/output"\n\n# Create output directory if it doesn't exist\nmkdir -p $OUTPUT_DIR\n\n# Get list of tables\nTABLES=$(mysql -u $DB_USER -p$DB_PASS $DB_NAME -e 'SHOW TABLES;' | awk '{ print $1}' | grep -v '^Tables' )\n\n# Export each table to a CSV file\nfor TABLE in $TABLES; do\n  mysql -u $DB_USER -p$DB_PASS $DB_NAME -e "SELECT * INTO OUTFILE '$OUTPUT_DIR/$TABLE.csv' FIELDS TERMINATED BY ',' ENCLOSED BY '\"' LINES TERMINATED BY '\n' FROM $TABLE;"\ndone