#!/bin/sh\n\n# Set the directory\ndir='some/directory'\n\n# List files using find, filter out directories, and choose a random file\nfile=$(find "$dir" -maxdepth 1 -type f | awk 'BEGIN {srand()} {files[NR] = $0} END {if (NR > 0) print files[int(NR * rand()) + 1]}')\n\n# Check if a file was found\nif [ -n "$file" ]; then\n  # Convert to full path\n  path=$(readlink -f "$file")\n  echo "The randomly-selected file is: $path"\nelse\n  echo "No files found in the directory."\nfi